import React, { useState } from "react";

const DynamicForm = () => {
  const [inputFields, setInputFields] = useState([{ name: "", age: "" }]);
  const submit = (e) => {
    e.preventDefault();
  };

  return (
    <div
      className="card"
      style={{ width: "600px", padding: "10px", margin: "20px auto" }}
    >
      <h3>Dynamic Form</h3>
      <form onSubmit={submit}>
        {inputFields.map((input, index) => {
          return (
            <div className="row mt-2" key={index}>
              <div className="col">
                <input
                  className="form-control"
                  type="text"
                  name="name"
                  placeholder="Name"
                />
              </div>
              <div className="col">
                <input
                  className="form-control"
                  type="text"
                  name="age"
                  placeholder="Age"
                />
              </div>
            </div>
          );
        })}
        <button className="btn btn-primary mt-2" onClick={submit}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default DynamicForm;
